<?php	session_start();	// $_POST['point'] = $_POST['point'];//分数	// $_POST['message'] //发送内容		if(!isset($_SESSION['access_token']))		header("Location: index.php");//没登陆就跳到授权登陆			require_once ('./class/requires.php');	include_once('./class/db.class.php');		//建立人人网对象	$client = new RenRenClient();	$session_key = $_SESSION['key']['renren_token']['session_key'];	$client->setSessionKey($session_key);	$db = new DB();//数据库对象		//json解包	for($i=1 ;$i<count($_POST['sendString']); $i++){		$_POST['json_en'][$i] = $_POST['sendString'][$i];		}		// 内容处理去链接。自己加链接	$contents=preg_replace('/<.+?>/', '', $_POST['message']);//正则替换	$_POST['shareContent'] = $contents . WEB_SIZE ;	//获取用户信息	$us =  $client->POST('users.getInfo',array("uid,name,sex,star,zidou,vip,birthday,tinyurl,headurl,mainurl,hometown_locationwork_history,university_history"));	//用户入库	$sql_arr = array();	$sql_arr['name'] = $us[0]['name'];		$sql_arr['point'] = $_POST['point'];		$sql_arr['sex'] = $us[0]['sex'];		$sql_arr['birthday'] = $us[0]['birthday'];		$sql_arr['university'] = $us[0]['university_history'][0]['name'];		$sql_arr['answers'] = json_encode($_POST['json_en']);		$sql_arr['time'] = time();		$db->insert('users',$sql_arr);		// 答案入库	$_POST['json_en']['uid'] = $db->insert_id();// 查出用户id	$db->insert('answers',$_POST['json_en']);		// 发送内容		// $resultusers = $client->POST('status.set',array($_POST['shareContent']));		//链接分享		$url = WEB_SIZE ;	$result = $client->POST('share.share',array(6,'','',$_POST['shareContent'], $url ));?>